use strict;
use warnings;
use List::Util qw/ pairmap sum /;
use feature qw/ say /;

use constant PART => $ARGV[0] // 1;

goto "PART_${\ PART}";

PART_1: {
    my @input = map { chomp; [split //] } <DATA>;
    my $xmas = 0;

    for my $y (0 .. $#input) {
        for my $x (0 .. $#{$input[$y]}) {
            my $char = $input[$y][$x]; 

            next unless $char eq 'X';

            my ($t, $r, $b, $l) = (q<>) x 4;
            $t = join q<>, grep { defined } map { $input[$y - $_][$x     ] } 1..3 if $y >= 3;
            $r = join q<>, grep { defined } map { $input[$y     ][$x + $_] } 1..3 if $x - 3 <= $#{$input[$y]};
            $b = join q<>, grep { defined } map { $input[$y + $_][$x     ] } 1..3 if $y - 3 <= $#input;
            $l = join q<>, grep { defined } map { $input[$y     ][$x - $_] } 1..3 if $x >= 3;

            my ($tr, $br, $bl, $tl) = (q<>) x 4;
            $tr = join q<>, grep { defined } map { $input[$y - $_][$x + $_] } 1..3 if $y >= 3 && $x - 3 <= $#{$input[$y]};
            $br = join q<>, grep { defined } map { $input[$y + $_][$x + $_] } 1..3 if $y - 3 <= $#input && $x - 3 <= $#{$input[$y]};
            $bl = join q<>, grep { defined } map { $input[$y + $_][$x - $_] } 1..3 if $y - 3 <= $#input && $x >= 3;
            $tl = join q<>, grep { defined } map { $input[$y - $_][$x - $_] } 1..3 if $y >= 3 && $x >= 3;

            $xmas += sum map { /MAS/ ? 1 : 0 } $t, $r, $b, $l, $tr, $br, $bl, $tl;
        }
    }

    say $xmas;

    exit 0;
}

PART_2: {
    my @input = map { chomp; [split //] } <DATA>;
    my $xmas = 0;

    for my $y (0 .. $#input) {
        for my $x (0 .. $#{$input[$y]}) {
            my $char = $input[$y][$x]; 

            next unless $char eq 'A';

            my ($tr, $br, $bl, $tl) = (q<>) x 4;
            $tr = $input[$y - 1][$x + 1] if $y > 0       && $x < $#{$input[$y]};
            $br = $input[$y + 1][$x + 1] if $y < $#input && $x < $#{$input[$y]};
            $bl = $input[$y + 1][$x - 1] if $y < $#input && $x > 0;
            $tl = $input[$y - 1][$x - 1] if $y > 0       && $x > 0;

            ++$xmas if
                ($tl . $br) =~ / SM | MS /x
             && ($tr . $bl) =~ / SM | MS /x;
        }
    }

    say $xmas;

    exit 0;
}

